        return 'Êåâ‰ΩèËØ¥ËØù';
      } else {
        return 'Êåâ‰ΩèËØ¥ËØù';
      }
    },
    /**
     * Ëé∑ÂèñËØ≠Èü≥ËæìÂÖ•Âå∫ÂüüÂç†‰ΩçÁ¨¶ÊñáÊú¨
     */
    getVoicePlaceholderText() {
      if (this.isListening) {
        return 'ÂΩïÈü≥‰∏≠';
      } else {
        return 'Êåâ‰ΩèËØ¥ËØù';
      }
    },
    /**
     * ÊòæÁ§∫ÊèêÁ§∫‰ø°ÊÅØ
     */
    showToast(message, type = 'info') {
      // ‰ΩøÁî®ÂÖ®Â±ÄtoastÊàñconsole
      if (this.$toast) {
        this.$toast[type]?.(message) || this.$toast(message);
      } else {
        console.log(`[${type.toUpperCase()}] ${message}`);
      }
    },
    /**
     * ÂàáÊç¢Âä†Âè∑ËèúÂçïÊòæÁ§∫Áä∂ÊÄÅ
     */
    togglePlusMenu() {
      this.showPlusMenu = !this.showPlusMenu;
      log.info('ÂàáÊç¢Âä†Âè∑ËèúÂçï', {
        show: this.showPlusMenu
      });
    },
    /**
     * ÊâìÂºÄÁõ∏ÂÜåÈÄâÊã©ÂõæÁâá
     */
    openPhotoLibrary() {
      this.showPlusMenu = false;
      this.$refs.imageInput.click();
      log.info('ÊâìÂºÄÁõ∏ÂÜåÈÄâÊã©ÂõæÁâá');
    },
    /**
     * ÊâìÂºÄÁõ∏Êú∫ÊãçÁÖß
     */
    openCamera() {
      this.showPlusMenu = false;
      // ÂàõÂª∫Áõ∏Êú∫ËæìÂÖ•ÔºåÊîØÊåÅÊãçÁÖß
      const cameraInput = document.createElement('input');
      cameraInput.type = 'file';
      cameraInput.accept = 'image/*';
      cameraInput.capture = 'camera';
      cameraInput.style.display = 'none';
      cameraInput.onchange = event => {
        const file = event.target.files[0];
        if (file) {
          this.handleImageSelect({
            target: {
              files: [file]
            }
          });
        }
        // Ê∏ÖÁêÜ‰∏¥Êó∂ÂÖÉÁ¥†
        document.body.removeChild(cameraInput);
      };
      document.body.appendChild(cameraInput);
      cameraInput.click();
      log.info('ÊâìÂºÄÁõ∏Êú∫ÊãçÁÖß');
    },
    /**
     * Â§ÑÁêÜÂõæÁâáÈÄâÊã©
     */
    handleImageSelect(event) {
      const file = event.target.files[0];
      if (!file) return;
      log.info('ÈÄâÊã©ÂõæÁâáÊñá‰ª∂', {
        fileName: file.name,
        fileSize: file.size,
        fileType: file.type
      });

      // È™åËØÅÂõæÁâáÊñá‰ª∂
      if (!file.type.startsWith('image/')) {
        this.showToast('ËØ∑ÈÄâÊã©ÊúâÊïàÁöÑÂõæÁâáÊñá‰ª∂', 'error');
        return;
      }

      // Áõ¥Êé•ÊâìÂºÄÂõæÁâáÂàÜÊûêÊ®°ÊÄÅÊ°Ü
      this.selectedFile = file;
      this.showImageAnalysisModal = true;

      // Ê∏ÖÁ©∫Êñá‰ª∂ËæìÂÖ•
      this.$refs.imageInput.value = '';
    },
    /**
     * ÊâìÂºÄÂõæÁâáÂàÜÊûêÊ®°ÊÄÅÊ°Ü
     */
    openImageAnalysis() {
      this.showImageAnalysisModal = true;
      log.info('ÊâìÂºÄÂõæÁâáÂàÜÊûêÊ®°ÊÄÅÊ°Ü');
    },
    /**
     * ÂÖ≥Èó≠ÂõæÁâáÂàÜÊûêÊ®°ÊÄÅÊ°Ü
     */
    closeImageAnalysisModal() {
      this.showImageAnalysisModal = false;
      log.info('ÂÖ≥Èó≠ÂõæÁâáÂàÜÊûêÊ®°ÊÄÅÊ°Ü');
    },
    /**
     * Â§ÑÁêÜÂõæÁâáÂàÜÊûêÂÆåÊàê
     */
    handleImageAnalysisComplete(result) {
      log.info('ÂõæÁâáÂàÜÊûêÂÆåÊàê', result);

      // Â∞ÜÂàÜÊûêÁªìÊûú‰Ωú‰∏∫Ê∂àÊÅØÂèëÈÄÅ
      const analysisMessage = `üñºÔ∏è ÂõæÁâáÂàÜÊûêÁªìÊûúÔºö\n\n${result.analysis.content}`;
      this.$emit('sendMessage', analysisMessage);

      // ÂÖ≥Èó≠Ê®°ÊÄÅÊ°Ü
      this.closeImageAnalysisModal();

      // ÊòæÁ§∫ÊàêÂäüÊèêÁ§∫
      this.showToast('ÂõæÁâáÂàÜÊûêÂÆåÊàêÔºåÂ∑≤ÂèëÈÄÅÁªìÊûúÔºÅ', 'success');
    }
  },
  beforeUnmount() {
    // Ê∏ÖÁêÜËµÑÊ∫ê
    if (this.isProcessing) {
      speechRecognitionService.resetProcessingStatus();
    }
    // ÂÅúÊ≠¢ÂΩïÈü≥ÊµÅ
    if (this.stream) {
      this.stream.getTracks().forEach(track => track.stop());
    }
    // ÂÅúÊ≠¢ËÆ°Êó∂Âô®
    this.stopRecordingTimer();
  }
};
