import axios from 'axios'
import { API_CONFIG } from '../config/api.js'

// ÈÖçÁΩÆÂü∫Á°ÄURL
const BASE_URL = API_CONFIG.baseURL

// ÂàõÂª∫axiosÂÆû‰æã
const api = axios.create({
  baseURL: BASE_URL,
  timeout: 10000,
  headers: {
    'Content-Type': 'application/json'
  }
})

// ‰ªªÂä°ÈõÜAPI
const collectionApi = {
  // Ëé∑Âèñ‰ªªÂä°ÈõÜÂàóË°®
  getCollections(params = {}) {
    return api.get('/collections', { params })
  },

  // Ëé∑ÂèñÂçï‰∏™‰ªªÂä°ÈõÜËØ¶ÊÉÖ
  getCollection(id) {
    return api.get(`/collections/${id}`)
  },

  // ÂàõÂª∫Êñ∞‰ªªÂä°ÈõÜ
  createCollection(data) {
    return api.post('/collections', data)
  },

  // Êõ¥Êñ∞‰ªªÂä°ÈõÜ
  updateCollection(id, data) {
    return api.put(`/collections/${id}`, data)
  },

  // Âà†Èô§‰ªªÂä°ÈõÜ
  deleteCollection(id, force = false) {
    return api.delete(`/collections/${id}`, {
      params: { force }
    })
  },

  // ÂΩíÊ°£/ÂèñÊ∂àÂΩíÊ°£‰ªªÂä°ÈõÜ
  archiveCollection(id, archived = true) {
    return api.put(`/collections/${id}/archive`, { archived })
  },

  // Ëé∑ÂèñÁªüËÆ°‰ø°ÊÅØ
  getStats(params = {}) {
    return api.get('/collections/stats', { params })
  },

  // Ëé∑Âèñ"ÂÖ∂‰ªñ"‰ªªÂä°ÈõÜ
  getOtherCollection(params = {}) {
    return api.get('/collections/other', { params })
  }
}

// ËØ∑Ê±ÇÊã¶Êà™Âô®
api.interceptors.request.use(
  (config) => {
    // Ê∑ªÂä†ËÆ§ËØÅtoken
    const token = localStorage.getItem('token')
    if (token) {
      config.headers = config.headers || {}
      config.headers['Authorization'] = `Bearer ${token}`
    }
    console.log(`üîµ APIËØ∑Ê±Ç: ${config.method?.toUpperCase()} ${config.url}`, config.data || config.params)
    return config
  },
  (error) => {
    console.error('‚ùå APIËØ∑Ê±ÇÈîôËØØ:', error)
    return Promise.reject(error)
  }
)

// ÂìçÂ∫îÊã¶Êà™Âô®
api.interceptors.response.use(
  (response) => {
    console.log(`üü¢ APIÂìçÂ∫î: ${response.config.method?.toUpperCase()} ${response.config.url}`, response.data)
    return response
  },
  (error) => {
    console.error('‚ùå APIÂìçÂ∫îÈîôËØØ:', error.response?.data || error.message)

    // Áªü‰∏ÄÈîôËØØÂ§ÑÁêÜ
    const message = error.response?.data?.message || error.message || 'ÁΩëÁªúÈîôËØØ'

    // 401 Â§ÑÁêÜÔºöË∑≥ËΩ¨ÁôªÂΩï
    if (error.response?.status === 401 && typeof window !== 'undefined') {
      localStorage.removeItem('token')
      localStorage.removeItem('user')
      window.location.href = '/auth/login'
    }

    // ÂèØ‰ª•Âú®ËøôÈáåÊ∑ªÂä†ÂÖ®Â±ÄÈîôËØØÊèêÁ§∫
    if (typeof window !== 'undefined' && window.app && window.app.$toast) {
      window.app.$toast.error(message)
    }

    return Promise.reject(error)
  }
)

export default collectionApi
