import speechRecognitionService from '../../AIsiri/services/speechRecognitionService.js';
import { log } from '../../AIsiri/utils/logger.js';
import ImageAnalysisModal from './ImageAnalysisModal.vue';
export default {
  name: 'MessageInput',
  components: {
    ImageAnalysisModal
  },
  data() {
    return {
      inputText: '',
      isListening: false,
      isProcessing: false,
      recognitionResult: null,
      recognitionStatusText: 'Ê≠£Âú®ËØÜÂà´‰∏≠...',
      selectedFile: null,
      showImageAnalysisModal: false,
      // Êñ∞Â¢ûÂΩïÈü≥Áõ∏ÂÖ≥Áä∂ÊÄÅ
      mediaRecorder: null,
      audioChunks: [],
      recordingTime: 0,
      recordingTimer: null,
      stream: null
    };
  },
  methods: {
    /**
     * ÂèëÈÄÅÊñáÊú¨Ê∂àÊÅØ
     */
    sendMessage() {
      if (!this.inputText.trim() || this.isProcessing) return;
      log.user('ÂèëÈÄÅÊñáÊú¨Ê∂àÊÅØ', {
        message: this.inputText
      });
      this.$emit('sendMessage', this.inputText);
      this.inputText = '';
    },
    /**
     * ÂàáÊç¢ËØ≠Èü≥ËæìÂÖ•Ê®°Âºè
     */
    async toggleVoiceInput() {
      if (this.isProcessing) return;
      if (this.isListening) {
        this.stopVoiceInput();
      } else {
        await this.startVoiceInput();
      }
    },
    /**
     * ÂºÄÂßãËØ≠Èü≥ËæìÂÖ•ÔºàÂÆûÊó∂ÂΩïÈü≥Ôºâ
     */
    async startVoiceInput() {
      try {
        log.info('ÂºÄÂßãÂÆûÊó∂ÂΩïÈü≥');

        // ËØ∑Ê±ÇÈ∫¶ÂÖãÈ£éÊùÉÈôê
        this.stream = await navigator.mediaDevices.getUserMedia({
          audio: {
            echoCancellation: true,
            noiseSuppression: true,
            sampleRate: 44100
          }
        });

        // ÂàõÂª∫MediaRecorder - ‰ΩøÁî®ÂêéÁ´ØÊîØÊåÅÁöÑÊ†ºÂºè
        let mimeType = 'audio/wav';

        // Ê£ÄÊü•ÊµèËßàÂô®ÊîØÊåÅÁöÑÊ†ºÂºèÔºå‰ºòÂÖà‰ΩøÁî®ÂêéÁ´ØÊîØÊåÅÁöÑÊ†ºÂºè
        if (MediaRecorder.isTypeSupported('audio/wav')) {
          mimeType = 'audio/wav';
        } else if (MediaRecorder.isTypeSupported('audio/mp4')) {
          mimeType = 'audio/mp4';
        } else if (MediaRecorder.isTypeSupported('audio/webm;codecs=opus')) {
          mimeType = 'audio/webm;codecs=opus';
        } else if (MediaRecorder.isTypeSupported('audio/webm')) {
          mimeType = 'audio/webm';
        }
        this.mediaRecorder = new MediaRecorder(this.stream, {
          mimeType: mimeType
        });

        // ÈáçÁΩÆÂΩïÈü≥Áä∂ÊÄÅ
        this.audioChunks = [];
        this.recordingTime = 0;

        // ÂºÄÂßãÂΩïÈü≥
        this.mediaRecorder.start();
        this.isListening = true;

        // ÂºÄÂßãËÆ°Êó∂
        this.startRecordingTimer();

        // ÁõëÂê¨ÂΩïÈü≥Êï∞ÊçÆ
        this.mediaRecorder.ondataavailable = event => {
          if (event.data.size > 0) {
            this.audioChunks.push(event.data);
          }
        };

        // ÂΩïÈü≥ÁªìÊùüÂ§ÑÁêÜ
        this.mediaRecorder.onstop = () => {
          this.handleRecordingComplete();
        };
        log.success('ÂÆûÊó∂ÂΩïÈü≥ÂºÄÂßã');
      } catch (error) {
        log.error('ÂêØÂä®ÂΩïÈü≥Â§±Ë¥•', error);
        this.showToast('Êó†Ê≥ïËÆøÈóÆÈ∫¶ÂÖãÈ£éÔºåËØ∑Ê£ÄÊü•ÊùÉÈôêËÆæÁΩÆ', 'error');

        // Â¶ÇÊûúÂÆûÊó∂ÂΩïÈü≥Â§±Ë¥•ÔºåÂõûÈÄÄÂà∞Êñá‰ª∂ÈÄâÊã©Ê®°Âºè
        this.fallbackToFileInput();
      }
    },
    /**
     * ÂÅúÊ≠¢ËØ≠Èü≥ËæìÂÖ•
     */
    stopVoiceInput() {
      if (this.mediaRecorder && this.isListening) {
        log.info('ÂÅúÊ≠¢ÂÆûÊó∂ÂΩïÈü≥');
        this.mediaRecorder.stop();
        this.isListening = false;
        this.stopRecordingTimer();

        // ÂÅúÊ≠¢È∫¶ÂÖãÈ£éÊµÅ
        if (this.stream) {
          this.stream.getTracks().forEach(track => track.stop());
          this.stream = null;
        }
      }
    },
    /**
     * ÂºÄÂßãÂΩïÈü≥ËÆ°Êó∂Âô®
     */
    startRecordingTimer() {
      this.recordingTimer = setInterval(() => {
        this.recordingTime++;
      }, 1000);
    },
    /**
     * ÂÅúÊ≠¢ÂΩïÈü≥ËÆ°Êó∂Âô®
     */
    stopRecordingTimer() {
      if (this.recordingTimer) {
        clearInterval(this.recordingTimer);
        this.recordingTimer = null;
      }
    },
    /**
     * Ê†ºÂºèÂåñÂΩïÈü≥Êó∂Èó¥
     */
    formatRecordingTime(seconds) {
      const mins = Math.floor(seconds / 60);
      const secs = seconds % 60;
      return `${mins.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
    },
    /**
     * Â§ÑÁêÜÂΩïÈü≥ÂÆåÊàê
     */
    async handleRecordingComplete() {
      try {
        if (this.audioChunks.length === 0) {
          this.showToast('ÂΩïÈü≥Â§±Ë¥•ÔºåÊ≤°ÊúâÂΩïÂà∂Âà∞Èü≥È¢ë', 'error');
          return;
        }

        // ÂàõÂª∫Èü≥È¢ëBlob
        const audioBlob = new Blob(this.audioChunks, {
          type: this.mediaRecorder.mimeType
        });

        // ËΩ¨Êç¢‰∏∫ÂêéÁ´ØÊîØÊåÅÁöÑÊ†ºÂºè
        const convertedAudioFile = await this.convertAudioFormat(audioBlob);
        log.info('ÂΩïÈü≥ÂÆåÊàêÔºåÂºÄÂßãÂ§ÑÁêÜ', {
          fileName: convertedAudioFile.name,
          fileSize: convertedAudioFile.size,
          duration: this.recordingTime,
          originalType: this.mediaRecorder.mimeType,
          convertedType: convertedAudioFile.type
        });

        // ÂºÄÂßãËØ≠Èü≥ËØÜÂà´
        await this.processAudioFile(convertedAudioFile);
      } catch (error) {
        log.error('Â§ÑÁêÜÂΩïÈü≥Â§±Ë¥•', error);
        this.showToast(`Â§ÑÁêÜÂΩïÈü≥Â§±Ë¥•: ${error.message}`, 'error');
      } finally {
        // Ê∏ÖÁêÜÁä∂ÊÄÅ
        this.audioChunks = [];
        this.recordingTime = 0;
        this.mediaRecorder = null;
      }
    },
    /**
     * ËΩ¨Êç¢Èü≥È¢ëÊ†ºÂºè‰∏∫ÂêéÁ´ØÊîØÊåÅÁöÑÊ†ºÂºè
     */
    async convertAudioFormat(audioBlob) {
      try {
        // Â¶ÇÊûúÂ∑≤ÁªèÊòØÊîØÊåÅÁöÑÊ†ºÂºèÔºåÁõ¥Êé•ËøîÂõû
        const supportedTypes = ['audio/wav', 'audio/mpeg', 'audio/mp4', 'audio/aac', 'audio/flac'];
        if (supportedTypes.includes(audioBlob.type)) {
          return new File([audioBlob], `recording_${Date.now()}.wav`, {
            type: audioBlob.type
          });
        }

        // ÂàõÂª∫Èü≥È¢ë‰∏ä‰∏ãÊñáËøõË°åÊ†ºÂºèËΩ¨Êç¢
        const audioContext = new (window.AudioContext || window.webkitAudioContext)();
        const arrayBuffer = await audioBlob.arrayBuffer();
        const audioBuffer = await audioContext.decodeAudioData(arrayBuffer);

        // ËΩ¨Êç¢‰∏∫WAVÊ†ºÂºè
        const wavBlob = this.audioBufferToWav(audioBuffer);

        // ÂàõÂª∫Êñ∞ÁöÑFileÂØπË±°
        const wavFile = new File([wavBlob], `recording_${Date.now()}.wav`, {
          type: 'audio/wav'
        });
        log.info('Èü≥È¢ëÊ†ºÂºèËΩ¨Êç¢ÂÆåÊàê', {
          originalType: audioBlob.type,
          convertedType: 'audio/wav',
          originalSize: audioBlob.size,
          convertedSize: wavFile.size
        });
        return wavFile;
      } catch (error) {
        log.error('Èü≥È¢ëÊ†ºÂºèËΩ¨Êç¢Â§±Ë¥•', error);
        // Â¶ÇÊûúËΩ¨Êç¢Â§±Ë¥•ÔºåÂ∞ùËØï‰ΩøÁî®ÂéüÂßãÊ†ºÂºè
        this.showToast('Èü≥È¢ëÊ†ºÂºèËΩ¨Êç¢Â§±Ë¥•Ôºå‰ΩøÁî®ÂéüÂßãÊ†ºÂºè', 'warning');
        return new File([audioBlob], `recording_${Date.now()}.wav`, {
          type: 'audio/wav'
        });
      }
    },
    /**
     * Â∞ÜAudioBufferËΩ¨Êç¢‰∏∫WAVÊ†ºÂºè
     */
    audioBufferToWav(buffer) {
      const length = buffer.length;
      const numberOfChannels = buffer.numberOfChannels;
      const sampleRate = buffer.sampleRate;
      const arrayBuffer = new ArrayBuffer(44 + length * numberOfChannels * 2);
      const view = new DataView(arrayBuffer);

      // WAVÊñá‰ª∂Â§¥
      const writeString = (offset, string) => {
        for (let i = 0; i < string.length; i++) {
          view.setUint8(offset + i, string.charCodeAt(i));
        }
      };
      writeString(0, 'RIFF');
      view.setUint32(4, 36 + length * numberOfChannels * 2, true);
      writeString(8, 'WAVE');
      writeString(12, 'fmt ');
      view.setUint32(16, 16, true);
      view.setUint16(20, 1, true);
      view.setUint16(22, numberOfChannels, true);
      view.setUint32(24, sampleRate, true);
      view.setUint32(28, sampleRate * numberOfChannels * 2, true);
      view.setUint16(32, numberOfChannels * 2, true);
      view.setUint16(34, 16, true);
      writeString(36, 'data');
      view.setUint32(40, length * numberOfChannels * 2, true);

      // ÂÜôÂÖ•Èü≥È¢ëÊï∞ÊçÆ
      let offset = 44;
      for (let i = 0; i < length; i++) {
        for (let channel = 0; channel < numberOfChannels; channel++) {
          const sample = Math.max(-1, Math.min(1, buffer.getChannelData(channel)[i]));
          view.setInt16(offset, sample < 0 ? sample * 0x8000 : sample * 0x7FFF, true);
          offset += 2;
        }
      }
      return new Blob([arrayBuffer], {
        type: 'audio/wav'
      });
    },
    /**
     * Â§ÑÁêÜÈü≥È¢ëÊñá‰ª∂ÔºàÂΩïÈü≥Êàñ‰∏ä‰º†Ôºâ
     */
    async processAudioFile(audioFile) {
      try {
        this.isProcessing = true;
        this.recognitionStatusText = 'Ê≠£Âú®‰∏ä‰º†Èü≥È¢ëÊñá‰ª∂...';
        log.info('ÂºÄÂßãËØ≠Èü≥ËØÜÂà´', {
          fileName: audioFile.name,
          fileSize: audioFile.size
        });

        // È™åËØÅÊñá‰ª∂
        const validation = speechRecognitionService.validateAudioFile(audioFile);
        if (!validation.isValid) {
          throw new Error(validation.errors.join(', '));
        }

        // ÂºÄÂßãËØ≠Èü≥ËØÜÂà´
        this.recognitionStatusText = 'Ê≠£Âú®ËØÜÂà´‰∏≠ÔºåËØ∑Á®çÂÄô...';
        const result = await speechRecognitionService.recognizeVoice(audioFile);
        if (result.success) {
          this.recognitionResult = result;
          this.recognitionStatusText = 'ËØÜÂà´ÂÆåÊàê';
          log.success('ËØ≠Èü≥ËØÜÂà´ÊàêÂäü', {
            transcription: result.transcription
          });

          // Ëá™Âä®Â°´ÂÖÖÂà∞ËæìÂÖ•Ê°Ü
          this.inputText = result.transcription;

          // ÊòæÁ§∫ÊàêÂäüÊèêÁ§∫
          this.showToast('ËØ≠Èü≥ËØÜÂà´ÊàêÂäüÔºÅ', 'success');
        } else {
          throw new Error('ËØÜÂà´Â§±Ë¥•');
        }
      } catch (error) {
        log.error('ËØ≠Èü≥ËØÜÂà´Â§±Ë¥•', error);
        this.recognitionStatusText = 'ËØÜÂà´Â§±Ë¥•';
        this.showToast(`ËØ≠Èü≥ËØÜÂà´Â§±Ë¥•: ${error.message}`, 'error');
      } finally {
        this.isProcessing = false;
        this.isListening = false;
        this.selectedFile = null;

        // Ê∏ÖÁ©∫Êñá‰ª∂ËæìÂÖ•
        if (this.$refs.fileInput) {
          this.$refs.fileInput.value = '';
        }
      }
    },
    /**
     * ÂõûÈÄÄÂà∞Êñá‰ª∂ÈÄâÊã©Ê®°Âºè
     */
    fallbackToFileInput() {
      log.info('ÂõûÈÄÄÂà∞Êñá‰ª∂ÈÄâÊã©Ê®°Âºè');
      this.$refs.fileInput.click();
    },
    /**
     * Â§ÑÁêÜÊñá‰ª∂ÈÄâÊã©ÔºàÂ§áÁî®ÊñπÊ°àÔºâ
     */
    async handleFileSelect(event) {
      const file = event.target.files[0];
      if (!file) {
        this.stopVoiceInput();
        return;
      }
      try {
        this.selectedFile = file;
        await this.processAudioFile(file);
      } catch (error) {
        log.error('Êñá‰ª∂Â§ÑÁêÜÂ§±Ë¥•', error);
        this.showToast(`Êñá‰ª∂Â§ÑÁêÜÂ§±Ë¥•: ${error.message}`, 'error');
      }
    },
    /**
     * ÁºñËæëËØÜÂà´ÁªìÊûú
     */
    editRecognitionResult() {
      if (this.recognitionResult) {
        this.inputText = this.recognitionResult.transcription;
        this.clearRecognitionResult();
        // ËÅöÁÑ¶Âà∞ËæìÂÖ•Ê°Ü
        this.$nextTick(() => {
          const input = this.$el.querySelector('.message-input');
          if (input) input.focus();
        });
      }
    },
    /**
     * ÂèëÈÄÅËØÜÂà´ÁªìÊûú
     */
    sendRecognitionResult() {
      if (this.recognitionResult) {
        this.inputText = this.recognitionResult.transcription;
        this.sendMessage();
        this.clearRecognitionResult();
      }
    },
    /**
     * Ê∏ÖÈô§ËØÜÂà´ÁªìÊûú
     */
    clearRecognitionResult() {
      this.recognitionResult = null;
    },
    /**
     * Ëé∑ÂèñËØ≠Èü≥ÊåâÈíÆÂõæÊ†á
     */
    getVoiceButtonIcon() {
      if (this.isProcessing) {
        return 'spinner';
      } else if (this.isListening) {
        return 'microphone-slash';
      } else {
        return 'microphone';
      }
    },
    /**
     * Ëé∑ÂèñËØ≠Èü≥ÊåâÈíÆÊ†áÈ¢ò
     */
    getVoiceButtonTitle() {
      if (this.isProcessing) {
        return 'Ê≠£Âú®ËØÜÂà´‰∏≠...';
      } else if (this.isListening) {
        return 'ÁÇπÂáªÂÅúÊ≠¢ÂΩïÈü≥';
      } else {
        return 'ÁÇπÂáªÂºÄÂßãËØ≠Èü≥ËæìÂÖ•';
      }
    },
    /**
     * ÊòæÁ§∫ÊèêÁ§∫‰ø°ÊÅØ
     */
    showToast(message, type = 'info') {
      // ‰ΩøÁî®ÂÖ®Â±ÄtoastÊàñconsole
      if (this.$toast) {
        this.$toast[type]?.(message) || this.$toast(message);
      } else {
        console.log(`[${type.toUpperCase()}] ${message}`);
      }
    },
    /**
     * ÊâìÂºÄÂõæÁâáÂàÜÊûêÊ®°ÊÄÅÊ°Ü
     */
    openImageAnalysis() {
      this.showImageAnalysisModal = true;
      log.info('ÊâìÂºÄÂõæÁâáÂàÜÊûêÊ®°ÊÄÅÊ°Ü');
    },
    /**
     * ÂÖ≥Èó≠ÂõæÁâáÂàÜÊûêÊ®°ÊÄÅÊ°Ü
     */
    closeImageAnalysisModal() {
      this.showImageAnalysisModal = false;
      log.info('ÂÖ≥Èó≠ÂõæÁâáÂàÜÊûêÊ®°ÊÄÅÊ°Ü');
    },
    /**
     * Â§ÑÁêÜÂõæÁâáÂàÜÊûêÂÆåÊàê
     */
    handleImageAnalysisComplete(result) {
      log.info('ÂõæÁâáÂàÜÊûêÂÆåÊàê', result);

      // Â∞ÜÂàÜÊûêÁªìÊûú‰Ωú‰∏∫Ê∂àÊÅØÂèëÈÄÅ
      const analysisMessage = `üñºÔ∏è ÂõæÁâáÂàÜÊûêÁªìÊûúÔºö\n\n${result.analysis.content}`;
      this.$emit('sendMessage', analysisMessage);

      // ÂÖ≥Èó≠Ê®°ÊÄÅÊ°Ü
      this.closeImageAnalysisModal();

      // ÊòæÁ§∫ÊàêÂäüÊèêÁ§∫
      this.showToast('ÂõæÁâáÂàÜÊûêÂÆåÊàêÔºåÂ∑≤ÂèëÈÄÅÁªìÊûúÔºÅ', 'success');
    }
  },
  beforeUnmount() {
    // Ê∏ÖÁêÜËµÑÊ∫ê
    if (this.isProcessing) {
      speechRecognitionService.resetProcessingStatus();
    }
    // ÂÅúÊ≠¢ÂΩïÈü≥ÊµÅ
    if (this.stream) {
      this.stream.getTracks().forEach(track => track.stop());
